let articleData=[];const fetchArticles=async()=>{await fetch("http://localhost:3000/api/cameras").then((a=>a.json())).then((a=>articleData=a))},displayArticles=async()=>{await fetchArticles();const a=articleData.map((a=>`\n            \n            <div class="col">\n            <div class="card effect">\n            <img src="${a.imageUrl}" class="card-img-top photo-change" alt="image de ${a.name}">\n            <div class="card-body">\n              <h2 class="card-title">${a.name}</h2>        \n              <p>${a.price/100}.00 â‚¬</p>\n             <div class=" consulter"><a href="./article.html?${a._id}" data-index-number=${a._id} class="btn btn-primary  stretched-link link-to-article ">Consulter</a></div>\n            </div>\n          </div>\n          </div>\n          \n    `)).join(" ");document.getElementById("card-contain").innerHTML+=a};displayArticles();